namespace IGT.Game.Core.Presentation.PeripheralLights.Devices
{
    /// <summary>
    /// The predefined color choices for the dual fade sequence.
    /// </summary>
    public enum DualFadeSequenceColor
    {
        /// <summary>
        /// Dim blue and bright blue.
        /// </summary>
        DimBlueBrightBlue = 0,

        /// <summary>
        /// Dim green and bright green.
        /// </summary>
        DimGreenBrightGreen = 1,

        /// <summary>
        /// Dim red and bright red.
        /// </summary>
        DimRedBrightRed = 2,

        /// <summary>
        /// Blue and green.
        /// </summary>
        BlueGreen = 3,

        /// <summary>
        /// Red and green.
        /// </summary>
        RedGreen = 4,

        /// <summary>
        /// Red and blue.
        /// </summary>
        RedBlue = 5,
    }
}
